mapper.update:
UPDATE TB_UM_DOWN_LOG
SET TEMP_TIME = now()
WHERE LOG_SN = #{logSn}

UPDATE TB_UM_DOWN_LOG
SET
    END_TIME = now(),
    SUCCESS_FLAG = #{flag}
WHERE
    LOG_SN = #{log_sn}

UPDATE TB_UM_DOWN_FILE_LOG
SET
    END_TIME = now(),
    SUCCESS_FLAG = #{flag}
WHERE
    FILE_NAME = #{file_name}
